import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ProgressBar } from 'react-bootstrap';

const ManageInventoryPage = () => {
  const [inventory, setInventory] = useState([]);
  const [newItem, setNewItem] = useState({
    name: '',
    store: '',
    address: '',
    pickupHour: '',
    quantity: '',
    expiryDate: '',
  });

  useEffect(() => {
    // Fetch inventory from server when component mounts
    axios
      .get('/inventory')
      .then(response => {
        setInventory(response.data);
      })
      .catch(error => {
        console.error('Error fetching inventory', error);
      });
  }, []);

  const handleChange = event => {
    setNewItem({
      ...newItem,
      [event.target.name]: event.target.value,
    });
  };

  const handleSubmit = event => {
    event.preventDefault();
    axios
      .post('/inventory', newItem)
      .then(response => {
        setInventory([...inventory, response.data]);
        setNewItem({
          name: '',
          store: '',
          address: '',
          pickupHour: '',
          quantity: '',
          expiryDate: '',
        });
      })
      .catch(error => {
        console.error('Error adding item', error);
      });
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '100vh', background: '#F0F0F0' }}>
      <h1 style={{ color: '#52B788', fontSize: '3em', marginBottom: '20px' }}>Manage Inventory</h1>
      <p style={{ color: '#52B788', fontSize: '1.2em', lineHeight: '1.5', textAlign: 'center', maxWidth: '600px', padding: '0 20px' }}>This is where you can add, view, and manage your inventory.</p>

      {/* Inventory form */}
      <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', marginBottom: '20px', width: '80%' }}>
        <input name="name" value={newItem.name} onChange={handleChange} placeholder="Item Name" required />
        <input name="store" value={newItem.store} onChange={handleChange} placeholder="Store Name" required />
        <input name="address" value={newItem.address} onChange={handleChange} placeholder="Store Address" required />
        <input name="pickupHour" value={newItem.pickupHour} onChange={handleChange} placeholder="Pickup Hours" required />
        <input name="quantity" value={newItem.quantity} onChange={handleChange} placeholder="Quantity" required />
        <input name="expiryDate" value={newItem.expiryDate} onChange={handleChange} placeholder="Expiry Date" required />
        <button type="submit">Add Item</button>
      </form>

      {/* Display inventory items */}
      <div style={{ marginTop: '50px', borderRadius: '20px', padding: '20px', background: '#FFFFFF', boxShadow: '0px 0px 15px rgba(0, 0, 0, 0.1)', width: '80%' }}>
        <h2 style={{ color: '#52B788', fontSize: '2em', marginBottom: '20px' }}>Your Inventory</h2>
        <table>
          <thead>
            <tr>
              <th>Item</th>
              <th>Store</th>
              <th>Address</th>
              <th>Pickup Hours</th>
              <th>Quantity</th>
              <th>Expiry Date</th>
              <th>Days until Expiry</th>
            </tr>
          </thead>
          <tbody>
            {inventory.map(item => (
              <tr key={item.id}>
                <td>{item.name}</td>
                <td>{item.store}</td>
                <td>{item.address}</td>
                <td>{item.pickupHour}</td>
                <td>{item.quantity}</td>
                <td>{item.expiryDate}</td>
                <td>
                  <ProgressBar variant={calculateDaysToExpiry(item.expiryDate) <= 7 ? 'danger' : 'success'} now={calculateDaysToExpiry(item.expiryDate)} max={30} />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

function calculateDaysToExpiry(date) {
  const currentDate = new Date();
  const expiryDate = new Date(date);
  const msPerDay = 1000 * 60 * 60 * 24;
  return Math.round((expiryDate - currentDate) / msPerDay);
}

export default ManageInventoryPage;
