import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import './../globalStyles.css';
import Navbar from '../components/Navbar';

const AboutUsPage = () => {
  const containerStyle = {
    maxWidth: '800px',
    margin: '200px auto 0',
    padding: '40px',
    background: '#f9f9f9',
    borderRadius: '8px',
    boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
  };

  const contentStyle = {
    marginBottom: '20px',
  };

  const paragraphStyle = {
    marginBottom: '10px',
    lineHeight: '1.6',
  };

  return (
    <div>
      <h1 className="home-page-title-top">PlateSaver</h1>
      <Navbar />
      <div style={containerStyle} className="content-center">
        <div style={contentStyle}>
          <h2 style={{ fontSize: '32px', fontWeight: 'bold', marginBottom: '20px' }}>
            About Us
          </h2>
          <p style={paragraphStyle}>
            Our mission is to serve as a conduit between local food stores and shelters, ensuring
            that no perishable item goes to waste and everyone gets a fair share.
          </p>
          {/* Rest of the content */}
        </div>
      </div>
      <Link to="/" className="home-link">
        Go back to Home
      </Link>
    </div>
  );
};

export default AboutUsPage;
