import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ProgressBar } from 'react-bootstrap';
import Navbar from '../components/Navbar';
import './../globalStyles.css';

const ManageInventoryPage = () => {
  const [inventory, setInventory] = useState([]);
  const [newItem, setNewItem] = useState({
    name: '',
    quantity: '',
    price: '',
    expiryDate: ''
  });

  useEffect(() => {
    // Fetch inventory from server when component mounts
    axios
      .get('/inventory')
      .then(response => {
        setInventory(response.data);
      })
      .catch(error => {
        console.error('Error fetching inventory', error);
      });
  }, []);

  const handleChange = event => {
    setNewItem({
      ...newItem,
      [event.target.name]: event.target.value
    });
  };

  const handleSubmit = event => {
    event.preventDefault();
    axios
      .post('/inventory', newItem)
      .then(response => {
        setInventory([...inventory, response.data]);
        setNewItem({
          name: '',
          quantity: '',
          price: '',
          expiryDate: ''
        });
      })
      .catch(error => {
        console.error('Error adding item', error);
      });
  };

  return (
    <div className="manage-inventory-page">
      <Navbar />
      <h1 className="manage-inventory-page-title">Manage Inventory</h1>
      <p className="manage-inventory-page-description">
        This is where you can add, view, and manage your inventory.
      </p>

      {/* Inventory form */}
      <form onSubmit={handleSubmit} className="inventory-form">
        <h2>Add Item to Inventory</h2>
        <input
          name="name"
          value={newItem.name}
          onChange={handleChange}
          placeholder="Item Name"
          required
        />
        <input
          name="quantity"
          value={newItem.quantity}
          onChange={handleChange}
          placeholder="Quantity"
          required
        />
        <input
          name="price"
          value={newItem.price}
          onChange={handleChange}
          placeholder="Price"
          required
        />
        <input
          name="expiryDate"
          value={newItem.expiryDate}
          onChange={handleChange}
          placeholder="Expiry Date"
          required
        />
        <button type="submit">Add Item</button>
      </form>

      {/* Display inventory items */}
      <div className="inventory-list">
        <h2 className="inventory-list-title">Your Inventory</h2>
        <table className="inventory-table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Quantity</th>
              <th>Price</th>
              <th>Expiry Date</th>
            </tr>
          </thead>
          <tbody>
            {inventory.map(item => (
              <tr key={item.id}>
                <td>{item.name}</td>
                <td>{item.quantity}</td>
                <td>{item.price}</td>
                <td>{item.expiryDate}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ManageInventoryPage;
