import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './../globalStyles.css';

const ManageInventoryPage = () => {
  const [inventory, setInventory] = useState([]);
  const [itemName, setItemName] = useState('');
  const [itemQuantity, setItemQuantity] = useState('');
  const [itemPrice, setItemPrice] = useState('');
  const [itemExpiryDate, setItemExpiryDate] = useState('');

  useEffect(() => {
    axios
      .get('/inventory')
      .then(response => {
        setInventory(response.data);
      })
      .catch(error => {
        console.error('Error fetching inventory', error);
      });
  }, []);

  const handleFormSubmit = (event) => {
    event.preventDefault();

    const newItem = {
      name: itemName,
      quantity: itemQuantity,
      price: itemPrice,
      expiryDate: itemExpiryDate
    };

    axios
      .post('/inventory', newItem)
      .then(response => {
        setInventory([...inventory, response.data]);
        setItemName('');
        setItemQuantity('');
        setItemPrice('');
        setItemExpiryDate('');
      })
      .catch(error => {
        console.error('Error adding item to inventory', error);
      });
  };

  return (
    <div className="manage-inventory-page">
      <h1 className="manage-inventory-page-title">Manage Inventory</h1>
      <p className="manage-inventory-page-description">
        This is where you can add, view, and manage your inventory.
      </p>
      <form onSubmit={handleFormSubmit}>
        {/* ... rest of your form code remains the same */}
      </form>
      {/* New table element for displaying inventory */}
      <table className="inventory-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Quantity</th>
            <th>Price</th>
            <th>Expiry Date</th>
          </tr>
        </thead>
        <tbody>
          {inventory.map(item => (
            <tr key={item.id}>
              <td>{item.name}</td>
              <td>{item.quantity}</td>
              <td>{item.price}</td>
              <td>{item.expiryDate}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <Link to={{
        pathname: '/meal-planning',
        state: {
          inventoryData: inventory
        }
      }} className="meal-planning-link">Go to Meal Planning</Link>
    </div>
  );
};

export default ManageInventoryPage;
