import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./../globalStyles.css";

const ManageInventoryPage = () => {
  const [inventory, setInventory] = useState([]);
  const [itemName, setItemName] = useState("");
  const [itemQuantity, setItemQuantity] = useState("");
  const [itemPrice, setItemPrice] = useState("");
  const [itemExpiryDate, setItemExpiryDate] = useState("");

  const handleFormSubmit = (event) => {
    event.preventDefault();

    const newItem = {
      id: Date.now(), // Add a unique ID to the item
      name: itemName,
      quantity: itemQuantity,
      price: itemPrice,
      expiryDate: itemExpiryDate,
    };

    setInventory([...inventory, newItem]);
    setItemName("");
    setItemQuantity("");
    setItemPrice("");
    setItemExpiryDate("");
  };

  return (
    <div className="manage-inventory-page">
      <h1 className="manage-inventory-page-title"></h1>
      <p className="manage-inventory-page-description">
    
      </p>
      <form onSubmit={handleFormSubmit} style={{ backgroundColor: '#f9f9f9', padding: '20px', borderRadius: '10px', border: '1px solid #ddd' }}>
  <h2 style={{ textAlign: 'center', fontSize: '24px' }}>List items for sale</h2>
  <input
    type="text"
    placeholder="Item Name"
    value={itemName}
    onChange={(event) => setItemName(event.target.value)}
  />
  <input
    type="number"
    placeholder="Item Quantity"
    value={itemQuantity}
    onChange={(event) => setItemQuantity(event.target.value)}
  />
  <input
    type="number"
    placeholder="Item Price"
    value={itemPrice}
    onChange={(event) => setItemPrice(event.target.value)}
  />
  <input
    type="date"
    value={itemExpiryDate}
    onChange={(event) => setItemExpiryDate(event.target.value)}
  />
  <button type="submit" style={{ display: 'inline-block', padding: '10px 20px', backgroundColor: '#007BFF', color: '#fff', border: 'none', borderRadius: '5px', cursor: 'pointer', marginTop: '10px' }}>Submit</button>
</form>
      <div className="inventory-list">
        <h2 className="inventory-list-title">Your Inventory</h2>
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Quantity</th>
              <th>Price</th>
              <th>Expiry Date</th>
            </tr>
          </thead>
          <tbody>
            {inventory.map((item) => (
              <tr key={item.id}>
                <td>{item.name}</td>
                <td>{item.quantity}</td>
                <td>{item.price}</td>
                <td>{item.expiryDate}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <Link
        to={{
          pathname: "/food-shelters",
          state: {
            inventoryData: inventory,
          },
        }}
        className="food-shelters-link"
      >
        Donate Food
      </Link>
    </div>
  );
};

export default ManageInventoryPage;
