import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';  // ensure Leaflet's CSS is imported

const locations = [
    { id: 1, name: "Shelter 1", position: [44.5654, -123.2620], type: "shelter" },
    { id: 2, name: "Shelter 2", position: [44.5646, -123.2639], type: "shelter" },
    { id: 3, name: "Shelter 3", position: [44.5240, -123.2919], type: "shelter" },
    { id: 4, name: "Shelter 4", position: [44.5854, -123.2740], type: "shelter" },
    { id: 5, name: "Shelter 5", position: [44.5036, -123.2029], type: "shelter" },
    { id: 6, name: "Shelter 6", position: [44.5926, -123.2209], type: "shelter" },

  { id: 3, name: "Oregon State University", position: [44.5646, -123.2793], type: "university" },  // OSU location
  // Add more locations here...
];

const FoodSheltersPage = () => {
  return (
    <div style={{ display: "flex", justifyContent: "center", alignItems: "center", height: "100vh" }}>
      <div style={{
        height: "600px",
        width: "800px",
        borderRadius: "15px",
        overflow: "hidden",
        border: "2px solid #333",
      }}>
        <MapContainer center={[44.5646, -123.2793]} zoom={13} style={{ height: "100%", width: "100%" }}>
          <TileLayer
            attribution='&copy; <a href="http://osm.org/copyright%22%3EOpenStreetMap</a> contributors'
            url="https://%7Bs%7D.tile.openstreetmap.org/%7Bz%7D/%7Bx%7D/%7By%7D.png"
          />
          {locations.map((location) => (
            location.type === "shelter" && (
              <Marker key={location.id} position={location.position}>
                <Popup>{location.name}</Popup>
              </Marker>
            )
          ))}
        </MapContainer>
      </div>
    </div>
  );
}
