import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './../globalStyles.css';

const ManageInventoryPage = () => {
  const [inventory, setInventory] = useState([]);
  const [itemName, setItemName] = useState('');
  const [itemCategory, setItemCategory] = useState('');
  const [itemQuantity, setItemQuantity] = useState('');
  const [itemPrice, setItemPrice] = useState('');    // New state variable for item price
  const [itemExpiryDate, setItemExpiryDate] = useState('');  // New state variable for item expiry date

  useEffect(() => {
    axios
      .get('/inventory')
      .then(response => {
        setInventory(response.data);
      })
      .catch(error => {
        console.error('Error fetching inventory', error);
      });
  }, []);

  const handleFormSubmit = (event) => {
    event.preventDefault();

    const newItem = {
      name: itemName,
      category: itemCategory,
      quantity: itemQuantity,
      price: itemPrice,       // Include price in item data
      expiryDate: itemExpiryDate  // Include expiry date in item data
    };

    axios
      .post('/inventory', newItem)
      .then(response => {
        setInventory([...inventory, response.data]);
        setItemName('');
        setItemCategory('');
        setItemQuantity('');
        setItemPrice('');   // Clear item price
        setItemExpiryDate(''); // Clear item expiry date
      })
      .catch(error => {
        console.error('Error adding item to inventory', error);
      });
  };

  return (
    <div className="manage-inventory-page">
      <h1 className="manage-inventory-page-title">Manage Inventory</h1>
      <p className="manage-inventory-page-description">
        This is where you can add, view, and manage your inventory.
      </p>
      <div className="inventory-list">
        <h2 className="inventory-list-title">Your Inventory</h2>
        {inventory.map(item => (
          <div key={item.id} className="inventory-list-item">
            <p>Name: {item.name}</p>
            <p>Category: {item.category}</p>
            <p>Quantity: {item.quantity}</p>
            <p>Price: {item.price}</p>
            <p>Expiry Date: {item.expiryDate}</p>
          </div>
        ))}
      </div>
      <form onSubmit={handleFormSubmit}>
        <h2>Add Item to Inventory</h2>
        <input
          type="text"
          placeholder="Item Name"
          value={itemName}
          onChange={event => setItemName(event.target.value)}
        />
        <input
          type="text"
          placeholder="Item Category"
          value={itemCategory}
          onChange={event => setItemCategory(event.target.value)}
        />
        <input
          type="number"
          placeholder="Item Quantity"
          value={itemQuantity}
          onChange={event => setItemQuantity(event.target.value)}
        />
        <input
          type="number"
          placeholder="Item Price"
          value={itemPrice}
          onChange={event => setItemPrice(event.target.value)}
        />
        <input
          type="date"
          placeholder="Expiry Date"
          value={itemExpiryDate}
          onChange={event => setItemExpiryDate(event.target.value)}
        />
        <button type="submit">Submit</button>
      </form>
      <Link to={{
        pathname: '/meal-planning',
        state: {
          inventoryData: inventory
        }
      }} className="meal-planning-link">Go to Meal Planning</Link>
    </div>
  );
};

export default ManageInventoryPage;
